    int angka = 0;
    private void btkurangActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btkurangActionPerformed
        angka--;
        if(angka < 0 ){
            JOptionPane.showMessageDialog(this,"Isi Dengan Benar","PERINGATAN",JOptionPane.WARNING_MESSAGE);
            angka = 0;
        }else {
            tengah.setText(String.valueOf(angka));
        }
    }//GEN-LAST:event_btkurangActionPerformed

    private void bttambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttambahActionPerformed
        angka ++;
        tengah.setText(Integer.toString(angka));
        tengah.setEditable(false);
    }//GEN-LAST:event_bttambahActionPerformed

    private void bttotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bttotalActionPerformed
        int RotiBantal = 10000, RotiSisir = 5000, RotiKukus = 15000, RotiNana = 25000, RotiLapis = 8000, RotiDonat = 30000, RotiSandwich = 20000, RotiCanai = 2500;
        int selai = 3000, kacang = 2000, cokelat = 4000, keju = 5000;
        
        int cash = 0, Total = 0, sisauang = 0;
        int HargaRoti = 0;
        int HargaToping = 0;
        
        if (rtbantal.isSelected()){
            HargaRoti = RotiBantal;
        }else if (rtsisir.isSelected()){
            HargaRoti = RotiSisir;
        }else if (rtkukus.isSelected()){
            HargaRoti = RotiKukus;
        }else if (rtnana.isSelected()){
            HargaRoti = RotiNana;
        }else if (rtlapis.isSelected()){
            HargaRoti = RotiLapis;
        }else if (rtdonat.isSelected()){
            HargaRoti = RotiDonat;
        }else if (rtsandwich.isSelected()){
            HargaRoti = RotiSandwich;
        }else if (rtcanai.isSelected()){
            HargaRoti = RotiCanai;
        }else{
            hasil.setText("0");
            tengah.setText("0");
            JOptionPane.showMessageDialog(null, "pilih menu terlebih dahulu");
        }
        
        if (tselai.isSelected()){
            HargaToping = selai;
        }else if (tkacang.isSelected()){
            HargaToping = kacang;
        }else if (tcokelat.isSelected()){
            HargaToping = cokelat;
        }else if (jRadioButton1.isSelected()){
            HargaToping = keju;
        }
        
        if (HargaRoti == 0 || HargaToping == 0){
            JOptionPane.showMessageDialog(null,"silahkan pilih menu roti dan toping terlebih dahlu","peringatan", JOptionPane.WARNING_MESSAGE);
        }
        
        Total = (HargaRoti + HargaToping)*angka;
        if (Total > 100000){
            Total *= 0.95;
            hasil.setText(Integer.toString(Total));
        }
        else if (Total < 100000){
            hasil.setText(Integer.toString(Total));
        }
        
        if (angka <= 0){
            JOptionPane.showMessageDialog(this, "masukkan jumlah yang anda ingin beli", "peringatan", JOptionPane.WARNING_MESSAGE);
            return;
        }
    }//GEN-LAST:event_bttotalActionPerformed

    private void CashKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_CashKeyReleased
        try{
            int Total = Integer.parseInt(hasil.getText());
            int untukKembalian = Integer.parseInt(Cash.getText());
            if (untukKembalian < Total){
                JOptionPane.showMessageDialog(null, "uangmu kurang kawan");
            }else if (untukKembalian > Total){
                int hasil = untukKembalian - Total;
                Kembali.setText(Integer.toString(hasil));
            }
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(null, "di isi pake nomer ya kawan");
        }
    }//GEN-LAST:event_CashKeyReleased

