    @SuppressWarnings("unchecked")
    private void perhitungan(){
        try {
            // Ambil nilai total pembayaran dan uang yang dibayar
            double totalPembayaran = Double.parseDouble(tfjumlah.getText());
            double uangDibayar = Double.parseDouble(tfcash.getText());

            // Hitung kembalian
            double kembalian = uangDibayar - totalPembayaran;

            // Cek apakah uang yang dibayar kurang
            if (kembalian < 0) {
                // Tampilkan JOptionPane dengan pesan "Uang kurang"
                JOptionPane.showMessageDialog(this, "Uang yang dibayar kurang!", "Error", JOptionPane.ERROR_MESSAGE);
            } else {
                // Tampilkan kembalian di text field 'tfkembali'
                tfkembali.setText(String.valueOf(kembalian));
            }
        } catch (NumberFormatException e) {
            // Jika input tidak valid, tampilkan pesan kesalahan
            JOptionPane.showMessageDialog(this, "Input tidak valid! Masukkan angka yang benar.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }
