    private void tfcashActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tfcashActionPerformed
        // TODO add your handling code here:
        try {
            // Ambil nilai total pembayaran dan uang yang dibayar (cash) dari text field
            double totalPembayaran = Double.parseDouble(tfjumlah.getText());
            double uangDibayar = Double.parseDouble(tfcash.getText());

            // Hitung kembalian
            double kembalian = uangDibayar - totalPembayaran;

            // Tampilkan kembalian di text field 'tfkembali'
            if (kembalian < 0) {
                tfkembali.setText("Uang kurang");
            } else {
                tfkembali.setText(String.valueOf(kembalian));
            }
        } catch (NumberFormatException e) {
            // Jika input tidak valid, tampilkan pesan kesalahan
            tfkembali.setText("Input tidak valid");
        }
    }//GEN-LAST:event_tfcashActionPerformed

